class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ret = 0;
        int m = queries.size();
        for (int i = 0, e = nums.size(); i < e; ++i) {
            int x = nums[i];
            if (x == 0)
                continue;
            set<int> vals{x};
            int j = 0;
            for (; j < m; ++j) {
                const auto& q = queries[j];
                if (q[0] <= i && q[1] >= i) {
                    for (auto v : vals) {
                        int new_v = v - q[2];
                        //cout << v << " " << new_v << "n";
                        if (new_v >= 0) {

                            vals.insert(new_v);
                        }
                    }
                }

                if (vals.count(0) == 1)
                    break;
            }
            //cout << "j=" << j << "n";
            if (j == m)
                return -1;
            ret = max(ret, j + 1);
        }
        return ret;
    }
};

