class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        res = 0
        totalReduced = 0

        @cache
        def minQueries(i, bound, rem):
            if rem == 0:
                return i
            if i == len(queries):
                return math.inf
            left, right = queries[i][0], queries[i][1]
            if bound < left or bound > right:
                return minQueries(i + 1, bound, rem)
            if rem - queries[i][2] < 0:
                return minQueries(i + 1, bound, rem)
            elif rem - queries[i][2] == 0:
                return i + 1
            else:
                return min(minQueries(i + 1, bound, rem), minQueries(i + 1, bound, rem - queries[i][2]))


        for i, num in enumerate(nums):
            res = max(res, minQueries(0, i, num))
            if res == math.inf:
                return -1
        return res

