class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<pair<int, int>>> cover(n); // For each index, store (val, query index)

        // Preprocess which queries cover each index
        for (int q_idx = 0; q_idx < m; ++q_idx) {
            int l = queries[q_idx][0];
            int r = queries[q_idx][1];
            int val = queries[q_idx][2];
            for (int i = l; i <= r; ++i) {
                cover[i].emplace_back(val, q_idx);
            }
        }

        vector<int> x(n, -1); // x[i] will hold the last query index needed for nums[i]
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                x[i] = -1;
                continue;
            }

            vector<pair<int, int>>& qs = cover[i];
            unordered_map<int, int> dp;
            dp[0] = -1; // Base case: sum 0 with no queries

            for (auto& p : qs) {
                int val = p.first;
                int idx = p.second;
                unordered_map<int, int> new_dp(dp);

                for (auto& entry : dp) {
                    int s = entry.first;
                    int last = entry.second;
                    int new_sum = s + val;
                    if (new_sum > nums[i]) {
                        continue; // Prune sums exceeding the target
                    }
                    auto it = new_dp.find(new_sum);
                    if (it == new_dp.end() || idx < it->second) {
                        new_dp[new_sum] = idx;
                    }
                }

                dp.swap(new_dp);
            }

            if (!dp.count(nums[i])) {
                return -1;
            }
            x[i] = dp[nums[i]];
        }

        int max_x = -1;
        for (int i = 0; i < n; ++i) {
            if (nums[i] != 0) {
                if (x[i] == -1) {
                    return -1;
                }
                if (x[i] > max_x) {
                    max_x = x[i];
                }
            }
        }

        return max_x == -1 ? 0 : max_x + 1;
    }
};

