class Solution {
public:

    int dp[1001][1001];

    int func(int i,int j,int sum,int n,int m,vector<int>& nums, vector<vector<int>>& q){
        if(sum==nums[i]) return 0;
        if(i==n || j==m){
            return 1e6;
        }
        if(dp[j][sum]!=-1) return dp[j][sum];

        int ans = 1e6;

        ans = min(ans,1+func(i,j+1,sum,n,m,nums,q));
        if(q[j][0]<=i and q[j][1]>=i and q[j][2]+sum<=nums[i]){
            ans = min(ans,1+func(i,j+1,sum+q[j][2],n,m,nums,q));
        }

        return dp[j][sum] = ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        int m = q.size();

        int ans = 0;

        for(int i=0;i<n;i++){
            memset(dp,-1,sizeof(dp));
            int curr = func(i,0,0,n,m,nums,q);
            if(curr==1e6) return -1;
            ans = max(ans,curr);
        }

        return ans;
    }
};

