class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        # binary search for k
        # nums len is only 10

        n = len(nums)
        vals = [[] for _ in range(n)]
        for iq, query in enumerate(queries):
            l, r, val = query
            for i in range(l, r + 1):
                vals[i].append((val, iq))

        # print(f'vals: {vals}')

        @cache
        def dp(nums_idx, k, curr_idx, target):
            # print(nums_idx, k, curr_idx, target)
            vls = vals[nums_idx]
            if target == 0:
                # print('2')
                return True
            if curr_idx >= len(vls) or vls[curr_idx][1] >= k:
                # print('1')
                return False
            if target < 0:
                # print('3')
                return False
            num = nums[i]

            # skip
            if dp(nums_idx, k, curr_idx + 1, target):
                return True

            # take
            if dp(nums_idx, k, curr_idx + 1, target - vls[curr_idx][0]):
                return True

            return False

        def possible(k):
            # check if subset of vals can equal num
            for i in range(n):
                if not dp(i, k, 0, nums[i]):
                    # print(f'i={i} not possible with k={k}')
                    return False
            return True

        left = 0
        right = len(queries) + 1

        found_ans = False
        while left < right:
            mid = (left + right) // 2
            # print(f'BINARY SEARCH left={left}, right={right}, mid={mid}')
            if possible(mid):
                # print(f'{mid} was possible, setting right from {right} to {mid}')
                right = mid
                found_ans = True
            else:
                # print(f'{mid} was not possible, setting left from {left} to {mid}')
                left = mid + 1
            # print('n===========n')

        dp.cache_clear()
        return left if found_ans else -1

