class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int n = nums.size();
        int m = queries.size();

        int sum = 0;
        for(auto &i : nums) sum += i;
        if(sum == 0) return 0;

        vector<int> dp(1001);

        auto get = [&](vector<int> &I, int x) ->int{
            for(auto &i : dp) i = 0;
            dp[0] = 1;
            for(int i = 0; i < I.size(); i++){
                int val = queries[I[i]][2];
                for(int j = x; j >= val; j--) dp[j] |= dp[j - val];
                if(dp[x]) return I[i];
            }
            return -1;
        };

        int ans = 0;
        for(int i = 0; i < n; i++){
            vector<int> I;
            for(int j = 0; j < m; j++)
                if(i >= queries[j][0] and i <= queries[j][1]) I.push_back(j);
            int res = get(I, nums[i]);
            if(res == -1) return -1;
            ans = max(ans, res + 1);
        }

        return ans;

    }
};

