class Solution {

    int n;
    List<Integer> ops;

    int getMin(int pos, int val, Integer [][] dp){

        if ( val == 0){
            return pos-1;
        }

        if ( pos == ops.size()){
            return Integer.MAX_VALUE;
        }

        if ( dp[pos][val] != null){
            return dp[pos][val];
        }

        int x1 = getMin(pos + 1, val, dp);
        if ( ops.get(pos) <= val){
            x1 =  Math.min(x1, getMin(pos + 1, val - ops.get(pos), dp));
        }

        return dp[pos][val] = x1;

    }

    int find(List<Integer> opSeq, int val){

        // we can include or exclude this operation
        ops = opSeq;
        Integer [][] dp = new Integer [opSeq.size()][val+1];
        int ret = getMin(0, val, dp);
        // System.out.println(opSeq + " with val " + val + " returned " + ret);
        return ret;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        n = nums.length;
        List<Integer> [] decs = new ArrayList [n];
        List<Integer> [] appliedAt = new ArrayList [n];
        for ( int i = 0 ; i < n ; i++){
            decs[i] = new ArrayList<>();
            appliedAt[i] = new ArrayList<>();
        }

        int idq = 1;
        for ( int [] q : queries){
            for ( int i = q[0]; i <= q[1]; i++){
                decs[i].add(q[2]);
                appliedAt[i].add(idq);
            }
            idq++;
        }

        int ans = 0;

        for ( int i = 0 ; i < n ; i++){
            if ( nums[i] == 0 ){
                ans = Math.max(ans, 0);
                continue;
            }
            int step = find(decs[i], nums[i]); // Minimum number of operation require to make it zero
            if ( step == Integer.MAX_VALUE){
                return -1; // not possible
            }
            ans = Math.max(ans, appliedAt[i].get(step));
        }

        return ans;
    }
}

