class Solution {
public:

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int ans = 0;
        for (int i = 0; i < n; i++){
            // set<int> choose;
            int target = nums[i];
            if (nums[i] == 0) continue;
            vector<bool> choose(target + 1);
            choose[0] = true;

            int j;
            for (j = 0; j < queries.size(); j++){
                if (queries[j][0] > i || queries[j][1] < i) continue;
                vector<bool> tmp = choose;
                for (int k = 0; k < choose.size(); k++){
                    if (k + queries[j][2] >= choose.size()) break;
                    if (choose[k])
                        tmp[k + queries[j][2]] = true;
                }
                choose = tmp;
                if (choose[target])
                    break;
            }
            if (choose[target])
                ans = max(ans, j + 1);
            else{
                // cout << i << 'n';
                return -1;
            }
        }
        return ans;
    }
};

