    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        n=nums.size();
        m=q.size();
        int s=0,e=m-1;
        int mid=(s+e)/2;
        bool f=0;
        for(int i=0;i<n;i++){
            if(nums[i]!=0) f=1;
        }
        if(!f) return 0;
        int ans=-1;
        while(s<=e){
            if(check(nums,q,mid)){
                ans=mid+1;
                e=mid-1;
            }
            else s=mid+1;
            mid=(s+e)/2;
        }
        return ans;
    }
};

