class Solution {
public:
    int valid(vector<int> nums, vector<vector<int>>& q,int f){
        if(f>=q.size()){
            return -1;
        }
        int sum=0;
        for(int i : nums){
            sum+=i;
        }
        if(sum==0){
            return f;
        }
        int n = (q[f][1]-q[f][0])+1;
        for (int i = 0; i < (1 << n); i++) {
            vector<int> subset;
            for (int j = 0; j < n; j++) {
                if ((i & (1 << j)) != 0) {
                    subset.push_back(j+q[f][0]);
                }
            }
            for(int &k : subset){
                nums[i]-=q[f][2];
            }
            int val = valid(nums,q,f+1);
            if(val!=-1){
                return val;
            }
            for(int &k : subset){
                nums[i]+=q[f][2];
            }
        }
        return -1;
    }
    int minZeroArray(vector<int>& a, vector<vector<int>>&b) {
        int c = a.size(), d = b.size();
        vector<bitset<1001>> e(a.size()), z(a.size());
        for (int i = 0; i < a.size(); i++) {
            e[i].reset();
            e[i].set(0, true);
            z[i].reset();
            for (int j = 0; j <= a[i]; j++){
              z[i].set(j, true);  
            } 
        }
        bool fFlag = true;
        for (int i = 0; i < c; i++) {
            if (a[i] != 0) { 
                fFlag = false; break;
            }
        }
        if (fFlag) return 0;
        for (int i = 0; i < d; i++) {
            int l = b[i][0]; 
            int r = b[i][1];
            int m = b[i][2];
            for (int j = l; j <= r; j++) e[j] |= ((e[j] << m) & z[j]);
            bool ok = true;
            for (int j = 0; j < c; j++) {
                if (!e[j].test(a[j])) { ok = false; break; }
            }
            if (ok) 
                return i + 1;
        }
        return -1;
    }
};

