class Solution {
    int dp[1005][1005];
    bool dfs(vector<int>& nums, int val, int n, int idx = 0) {
        if(idx == n)
            return val == 0;

        if(dp[idx][val] != -1)
            return dp[idx][val];

        bool ans = dfs(nums, val, n, idx + 1);

        if(val >= nums[idx]) {
            ans |= dfs(nums, val - nums[idx], n, idx + 1);
        }

        return dp[idx][val] = ans;
    }
    bool possible(vector<int>& nums, vector<vector<int>>& queries, int mid) {
        int n = nums.size();

        vector<vector<int>> red(n);
        for(int i = 0; i <= mid; i++) {
            for(int j = queries[i][0]; j <= queries[i][1]; j++) {
                if(queries[i][2] <= nums[j])
                    red[j].push_back(queries[i][2]);
            }
        }

        for(int i = 0; i < n; i++) {
            memset(dp, -1, sizeof(dp));
            bool isAvailable = dfs(red[i], nums[i], red[i].size());

            if(!isAvailable)
                return false;
        }

        return true;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool okay = true;
        for(auto x:nums) {
            if(x) okay = false;
        }

        if(okay) return 0;
        int l = 0, r = queries.size() - 1, ans = -1;

        while(l <= r) {
            int mid = (l + r) / 2;

            if(possible(nums, queries, mid)) {
                ans = mid + 1;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }

        return ans;
    }
};

