class Solution {
    static Boolean f(int n, int target, ArrayList<Integer> temp, Boolean[][] memo) {
        if (target == 0) return true; 
        if (n == 0) return false;     

        if (memo[n][target] != null) {
            return memo[n][target];
        }

        if (temp.get(n-1) <= target) {
            if (f(n-1, target - temp.get(n-1), temp, memo)) {
                memo[n][target] = true;
                return true;
            }
        }

        memo[n][target] = f(n-1, target, temp, memo);
        return memo[n][target];
    }
    public boolean check(int a[], int q[][], int mid) {
        ArrayList<ArrayList<Integer>> list = new ArrayList<>();
        for (int i = 0; i < a.length; i++) {
            list.add(new ArrayList<>());
        }
        for (int i = 0; i <= mid; i++) {
            for (int j = q[i][0]; j <= q[i][1]; j++) {
                list.get(j).add(q[i][2]);
            }
        }
        for (int i = 0; i < a.length; i++) {
            ArrayList<Integer> temp = list.get(i);
            Boolean[][] memo = new Boolean[temp.size() + 1][a[i] + 1];
            if (!f(temp.size(), a[i], temp, memo)) {
                return false;
            }
        }
        return true;
    }
    public int minZeroArray(int[] a, int[][] q) {
        int n=a.length;
        boolean flag=true;
        for(int i=0;i<n;i++){
            if(a[i] != 0){
                flag=false;
                break;
            }
        }
        if(flag) return 0;
        int l=0;
        int h=q.length-1;
        int ans=-1;
        while(l <= h){
            int mid=(l+h)/2;
            if(check(a,q,mid)){
                ans=mid+1;
                h=mid-1;
            }else{
                l=mid+1;
            }
        }
        return ans;
    }
}

