public:
    bool chk(vector<vector<pair<int,int>>>&v,int m,vector<int>&nums){
        for(int i=0;i<nums.size();++i){
            vector<bool>vv(nums[i]+1,0);
            vv[0]=1;
            for(auto &[k,e]:v[i]){
                if(k<m){
                    for(int j=nums[i];j>=e;--j){
                        if(vv[j-e]){
                            vv[j]=1;
                        }
                    }
                } else {
                    break;
                }
            }
            if(!vv[nums[i]]) return 0;
        }
        return 1;
    }
