        vector<vector<pair<int,int>>> v(nums.size());
        for(int i=0;i<queries.size();++i){
            auto e=queries[i];
            for(int j=e[0];j<=e[1];++j){
                v[j].push_back({i,e[2]});
            }
        }
        int l=0,r=queries.size()+1,ans=-1;

        while(l<r){
            int m=(l+r)/2;
            if(chk(v,m,nums)){
                r=m;
                ans=m;
            }else {
                l=m+1;
            }

        }

        return ans;
    }
