    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        def pos1(dd, x):
            #print(dd,x)
            arr = [False]*1001
            arr[0] = True
            for k,v in dd.items():
                for i in range(v):
                    for j in reversed(range(k,1001)):
                        arr[j] |= arr[j-k]
                        if arr[x]:
                            return True
            return arr[x]

        def pos(k):
            dd = collections.defaultdict(int)
            ll = []
            for i,j,v in queries[:k]:
                ll.append((i,v,+1))
                ll.append((j+1,v,-1))
            ll = sorted(ll, reverse=True)
            for i,x in enumerate(nums):
                while ll and ll[-1][0]<=i:
                    _,v,add = ll.pop()
                    dd[v] += add
                if pos1(dd,x):
                    continue
                return False
            return True

        class sol:
            def __getitem__(self, i):
                return pos(i)

        s = sol()
        j = bisect.bisect_left(s,True,0,len(queries)+2)
        return -1 if s[j]==False else j

