#include <bits/stdc++.h>
using namespace std;


class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int max_num = *max_element(nums.begin(), nums.end());
        vector<vector<bool>> mem(nums.size(), vector<bool>(max_num + 1, false));

        // Find zero elements
        for (int i = 0; i < nums.size(); i++) {
            mem[i][0] = true;
        }
        int done = count(nums.begin(), nums.end(), 0);
        if (done == nums.size()) return 0;

        // Apply queries
        for (int k = 0; k < queries.size(); k++) {
            // Update all numbers in range
            for (int i = queries[k][0]; i <= queries[k][1]; i++) {
                bool was_done = mem[i][nums[i]];

                // Move backward in memory
                int val = queries[k][2];
                for (int j = max_num; j >= val; j--) {
                    if (mem[i][j - val]) mem[i][j] = true;
                }

                // Check if there is a new combination equal to nums[i]
                if (!was_done && mem[i][nums[i]]) {
                    done++;
                }
            }


            if (done == nums.size()) {
                return k + 1;
            }
        }

        return -1;
    }
};

