class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int i,j,n=nums.size(),m=queries.size(),ans=INT_MIN;
        int num,l,r,val;

        for(i=0;i<n;i++){
            num=nums[i];
            unordered_set<int> myset1,myset2;
            myset1.insert(0);

            //check when num is 0
            if(num == 0){
                ans=max(ans,0);
                continue;
            }


            for(j=0;j<m;j++){
                l=queries[j][0];
                r=queries[j][1];
                val=queries[j][2];

                //out of range
                if(i>r || i<l)
                    continue;

                //update reachable states
                for(auto itr:myset1){
                    myset2.insert(itr);
                    if(itr+val <= num)
                        myset2.insert(itr+val);
                }

                myset1=myset2;
                myset2.clear();

                //we can reach num
                if(myset1.count(num))
                    break;
            }

            //not possible case
            if(j==m){
                return -1;
            }
            ans=max(ans,j+1);

        }


        return ans;
    }
};

