class Solution {
public:
    int minZeroArray(vector<int>& arr, vector<vector<int>>& ops) {
        int n = arr.size();
        vector<vector<bool>> dp(n, vector<bool>(1001, false));

        for (int i = 0; i < n; i++) dp[i][0] = true;

        if (isAllZero(arr)) return 0;

        for (int idx = 0; idx < ops.size(); idx++) {
            if (applyOp(arr, dp, ops[idx])) {
                return idx + 1;
            }
        }

        return -1;
    }

private:
    bool isAllZero(const vector<int>& arr) {
        for (int val : arr) {
            if (val) return false;
        }
        return true;
    }

    bool applyOp(vector<int>& arr, vector<vector<bool>>& dp, const vector<int>& op) {
        int l = op[0], r = op[1], v = op[2];

        for (int i = l; i <= r; i++) {
            for (int j = 1000 - v; j >= 0; j--) {
                if (dp[i][j]) {
                    dp[i][j + v] = true;
                }
            }
            for (int p = arr[i] + 1; p < 1001; p++) dp[i][p] = false;
        }

        for (int i = 0; i < arr.size(); i++) {
            if (!dp[i][arr[i]]) {
                return false;
            }
        }
        return true;
    }
};

