class Solution {
public:
    const int INF = INT_MAX / 2; 
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qs) {
        int ret = 0;
        for(int i = 0;i<nums.size();i++) {
            vector<int> xs; 
            xs.push_back(0);
            for(int j = 1;j<=qs.size();j++) {
                auto& q = qs[j-1];
                if(q[0] <= i && i <= q[1]) {
                    xs.push_back(q[2]);
                } else {
                    xs.push_back(0);
                }
            }
            // cout<<"i= "<<i<<" xs = [";
            // for(int x : xs) {
            //     cout<<x<<" ";
            // }
            // cout<<"]"<<endl;
            const int V = nums[i];
            vector<vector<int>> dp(xs.size(), vector<int>(V + 1, INF));
            for(int i = 0;i<dp.size();i++) {
                dp[i][0] = 0;
            }

            for(int i = 1;i<dp.size();i++) {
                //cout<<"i- "<<i<<endl;
                for(int j = 1;j<=V;j++) {
                    //cout<<"i= "<<i<<" j= "<<j<<" xs[i]= "<<xs[i]<<endl;
                    dp[i][j] = dp[i-1][j]; 
                    if(j >= xs[i]) {
                        int pre = dp[i-1][j - xs[i]];
                        //cout<<"i= "<<i<<" j= "<<j<<" pre= "<<pre<<endl;
                        dp[i][j] = min(dp[i][j], max(i, pre));
                    }
                }
            }

            //cout<<"i= "<<i<<" nums[i]= "<<nums[i]<<" dp: "<<endl;
            // for(int i = 0;i<dp.size();i++) {
            //     for(int j = 0;j<dp[i].size();j++) {
            //         cout<<dp[i][j]<<" ";
            //     }
            //     cout<<endl;
            // }

            int res = dp[dp.size()-1][nums[i]];
            //cout<<"i= "<<i<<" nums[i]= "<<nums[i]<<" res= "<<res<<endl;
            ret = max(ret, res);
            if(ret >= INF) {
                return -1;
            }
        }
        return ret; 
    }
};

