class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        int maxK = -1;

        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) continue;

            List<int[]> applicable = new ArrayList<>();
            for (int j = 0; j < m; j++) {
                int l = queries[j][0], r = queries[j][1];
                if (i >= l && i <= r) {
                    applicable.add(new int[]{j, queries[j][2]});
                }
            }

            if (applicable.isEmpty()) {
                return -1;
            }

            Set<Integer> sumPossible = new HashSet<>();
            sumPossible.add(0);
            int earliestJ = -1;

            for (int[] q : applicable) {
                int j = q[0], val = q[1];
                Set<Integer> newSums = new HashSet<>(sumPossible);
                for (int s : sumPossible) {
                    newSums.add(s + val);
                }
                if (newSums.contains(target)) {
                    earliestJ = j;
                    break;
                }
                sumPossible = newSums;
            }

            if (earliestJ == -1) {
                return -1;
            }
            if (earliestJ > maxK) {
                maxK = earliestJ;
            }
        }

        return maxK == -1 ? 0 : maxK + 1;
    }
}

