from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        # Initialize DP as sets
        dp = [set() for _ in range(n)]
        for j in range(n):
            dp[j].add(0)

        # Check if all elements are zero initially
        if all(num == 0 for num in nums):
            return 0

        for k, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                # Update DP using new values
                new_values = {x + v for x in dp[j]}
                dp[j].update(new_values)

                # Reset values greater than nums[j]
                dp[j] = {x for x in dp[j] if x <= nums[j]}

            # Check if all values are achieved
            if all(nums[j] in dp[j] for j in range(n)):
                return k + 1

        return -1

