class Solution {
public:
    int solve(int num, int k, int i, vector<vector<int>>& queries , vector<vector<int>>& dp){
        if(num == 0) return 1;
        if(num < 0 || k < 0) return 0;
        if(dp[num][k] != -1) return dp[num][k];

        dp[num][k] = solve(num, k - 1, i, queries, dp);
        if(i >= queries[k][0] && i <= queries[k][1]){
            dp[num][k] |= solve(num - queries[k][2], k - 1, i, queries, dp);
        }
        return dp[num][k];
    }
    bool isPossible(vector<int>& nums, vector<vector<int>>& queries, int mid){
        bool res = true;
        for(int i = 0; i < nums.size(); i++){
            vector<vector<int>> dp(nums[i] + 1, vector<int> (mid, -1));
            res &= solve(nums[i], mid-1, i, queries, dp);
            if(!res) return res;
        }
        return res;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int start = 0, end = queries.size();
        int res = -1;
        while(start < end){
            int mid = start + (end - start)/2;
            if(isPossible(nums,queries,mid)) {
                end = mid - 1;
                res = mid;
            }
            else start = mid + 1;
        }
        if(isPossible(nums,queries,start)) res = start;
        return res;
    }
};

