#include <vector>
#include <bitset>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& array, vector<vector<int>>& operations) {
        int length = array.size();
        vector<bitset<1001>> state(length);
        for (int i = 0; i < length; i++) 
            state[i].set(0);

        bool allElementsZero = true;
        for (int value : array) {
            if (value != 0) {
                allElementsZero = false;
                break;
            }
        }
        if (allElementsZero) return 0;


        for (int op = 0; op < operations.size(); op++) {
            int start = operations[op][0];
            int end = operations[op][1];
            int increment = operations[op][2];

            for (int i = start; i <= end; i++) {
                bitset<1001> temp = state[i];
                temp = shiftLeft(temp, increment);
                state[i] |= temp;

                for (int pos = array[i] + 1; pos < 1001; pos++) {
                    state[i].reset(pos);
                }
            }

            bool targetAchieved = true;
            for (int i = 0; i < length; i++) {
                if (!state[i].test(array[i])) {
                    targetAchieved = false;
                    break;
                }
            }

            if (targetAchieved) return op + 1;
        }

        return -1;
    }

    int solve(vector<int>& array , int b){
        return array[0]+b;
    }

public:
    bitset<1001> shiftLeft(const bitset<1001>& bitSet, int shift) {
        bitset<1001> shifted;
        for (int i = 0; i < 1001 - shift; i++) {
            if (bitSet.test(i)) {
                shifted.set(i + shift);
            }
        }
        return shifted;
    }
};

