class Solution {
    int fans=100000;
    int sumReq=-1;
    int dp[][];
    int go(int ind, int st, int qi, int[][] qeuries){
        if(st==sumReq){
            // fans = Math.min(fans,ind);
            return ind;
        }
        if(st>sumReq)return Integer.MAX_VALUE;
        if(ind>=qeuries.length)return Integer.MAX_VALUE;
        if(dp[ind][st]!=-1)return dp[ind][st];
        if(qi >= qeuries[ind][0] && qi <= qeuries[ind][1])
            return dp[ind][st] = Math.min(go(ind+1,st+qeuries[ind][2],qi,qeuries), 
                go(ind+1,st,qi,qeuries));
        else return dp[ind][st] = go(ind+1,st,qi,qeuries);
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int ind=0;
        int ans=-1;
        // dp[i][sumtaken] - till i, sumtaken, 
        // returns trueor false if till i, we can make sumtaken
        // dp[i][st] = dp[i+1][st+a[i]] || dp[i+1][st]
        // if i==n return st==reqSum
        int n = queries.length;
        int mx=-1;
        for(int x:nums)mx=Math.max(mx,x);
        for(int x:nums){
            sumReq = x;
            // fans=100000;
            dp = new int[n+1][mx+1];
            for(int i=0;i<=n;i++){
                for(int j=0;j<=mx;j++)dp[i][j]=-1;
            }
            fans = go(0,0,ind,queries);
            // fans = dp[0][0];
            // System.out.println(ind + " " + fans);
            if(fans!=Integer.MAX_VALUE)ans = Math.max(ans,fans);
            else return -1;
            ind++;
        }
        return ans;
    }
}

