class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int mx = *max_element(nums.begin(),nums.end());
        vector<vector<int>> dp(queries.size(),vector<int>(mx+1,0));
        reverse(queries.begin(),queries.end());
        int ans = 0;
        for(int i = 0;i < nums.size();i++){
            int req = nums[i];
            if(!req) continue;
            int currK = -1;
            dp = vector<vector<int>>(queries.size()+1,vector<int>(req+1,0));
            for(int j = 0;j <= queries.size();j++) dp[j][0] = 1;
            for(int j = queries.size()-1;j >= 0;j--){
                for(int x = 0;x < req+1;x++){
                    // Take
                    if(queries[j][0]<=i && queries[j][1]>=i && queries[j][2]<=x)
                    dp[j][x] = max(dp[j][x],dp[j+1][x-queries[j][2]]);
                    // notTake    
                    dp[j][x] = max(dp[j][x],dp[j+1][x]);
                }
                if(dp[j][req] == 1){
                    currK = j;
                    break;
                }
            }
            if(currK == -1) return -1;
            ans = max(ans,(int)queries.size()-currK);
        }
        return ans;
    }
};

