class Solution {

private:

    int getMinQuery(vector<int>& arr, int i, int sumTillNow, vector<vector<int>>& dp) {

        if (sumTillNow == 0) {
            return 0;
        }

        if (i >= arr.size()) {
            return INT_MAX;
        }

        if (sumTillNow - arr[i] == 0) {
            return i+1;
        }

        if (dp[i][sumTillNow] != -1) {
            return dp[i][sumTillNow];
        }

        int op1 = getMinQuery(arr, i+1, sumTillNow, dp);
        int op2 = INT_MAX;

        if (sumTillNow - arr[i] > 0) {
            op2 = getMinQuery(arr, i+1, sumTillNow-arr[i], dp);
        }

        return dp[i][sumTillNow] = min(op1, op2);

    }


public:

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        vector<vector<int>> idxToArr(nums.size());

        for (auto q: queries) {

            int l = q[0];
            int r = q[1];
            int val = q[2];

            for (int i=0; i<nums.size(); i++) {
                if (i >= l && i <= r) {
                    idxToArr[i].push_back(val);
                }
                else {
                    idxToArr[i].push_back(0);
                }
            }

        }

        int maxQuery = INT_MIN;

        for (int i=0; i<nums.size(); i++) {
            vector<vector<int>> dp(idxToArr[i].size()+1, vector<int>(nums[i]+1, -1));
            int minQuery = getMinQuery(idxToArr[i], 0, nums[i], dp);
            maxQuery = max(maxQuery, minQuery);
        }

        return maxQuery != INT_MAX ? maxQuery : -1;


    }

};

