class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        def go(i, v):
            a = [0] * m
            for k, (l, r, val) in enumerate(queries):
                if l <= i and i <= r:
                    a[k] = val

            @cache
            def dp(k, r):
                if r == 0:
                    return k
                if k == m:
                    return m + 1
                tmp = dp(k+1, r)
                if a[k] <= r:
                    tmp = min(tmp, dp(k+1, r-a[k]))
                return tmp
            return dp(0, v)


        ans = [-1] * n
        for i in range(n):
            ans[i] = go(i, nums[i])
            if ans[i] >= m + 1:
                return -1
        return max(ans)

