class Solution:
    debugging = False

    def is_subset_sum(self, val_counts: dict[int, int], target: int) -> bool:
        '''Return True iff there's a way to select values from val_counts
        such that they sum up to target'''
        vals = list(val_counts.keys())

        @cache
        def is_target_sum_from(cur_val_idx: int, target_remaining: int) -> bool:
            if target_remaining == 0:
                return True
            if cur_val_idx == len(vals):
                return False
            cur_val = vals[cur_val_idx]
            for num_val_to_use in range(val_counts[cur_val]+1):
                next_target = target_remaining - num_val_to_use*cur_val
                if next_target < 0:
                    break
                can_get_target_later = is_target_sum_from(
                    cur_val_idx+1, next_target)
                if can_get_target_later:
                    return True
            return False

        return is_target_sum_from(cur_val_idx=0, target_remaining=target)

    def can_zero(self, nums: List[int], queries: List[List[int]]) -> bool:
        '''Return True iff we can zero all of nums with the given queries
        For each query, add the val to a counter for each num it affects'''
        val_counts_by_num = [defaultdict(int) for _ in range(len(nums))]
        for l_idx, r_idx, val in queries:
            for mid_idx in range(l_idx, r_idx+1):
                val_counts_by_num[mid_idx][val] += 1
        val_counts_by_num = [dict(counts) for counts in val_counts_by_num]
        if self.debugging:
            print(f't{queries=} -> {val_counts_by_num=}')
        for num_idx in range(len(nums)):
            cur_num = nums[num_idx]
            if cur_num == 0:
                continue
            can_sum = self.is_subset_sum(val_counts_by_num[num_idx], cur_num)
            if not can_sum:
                return False
        return True

    def binary_search_approach(self, nums: List[int], queries: List[List[int]]) -> int:
        '''Outer loop: try to zero all of nums with just queries[:k]'''
        low = 1
        high = len(queries)
        while low <= high:
            mid = (low + high) // 2
            can_zero = self.can_zero(nums, queries[:mid])
            if self.debugging:
                print(f'{low=}, {high=} -> {mid=}, {can_zero=}')
            if can_zero:
                high = mid - 1
            else:
                low = mid + 1
        if self.debugging:
            print(f'final {low=}, {high=}')
        return low if low <= len(queries) else -1

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(num == 0 for num in nums):
            return 0
        return self.binary_search_approach(nums, queries)

