class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int l = -1;
        int r = m - 1;
        int mid = 0;

        auto check = [&](int mid) -> bool {
            vector<vector<int>> tmp(n);
            for (int i = 0; i <= mid; i++) {
                int l = queries[i][0];
                int r = queries[i][1];
                int v = queries[i][2];
                for (int j = l; j <= r; j++) {
                    tmp[j].push_back(v);
                }
            }

            return subsetSum(nums, tmp);

        };

        while (l <= r) {
            mid = l + (r - l) / 2;
            if (check(mid) == false) {
                l = mid + 1;
            } else {
                r = mid - 1;
            }
        }

        return l == m ? -1 : l + 1;    
    }

    bool subsetSum(vector<int>& nums, vector<vector<int>>& tmp) {
        int n = nums.size();

        for (int i = 0; i < n; i++) {
            int target = nums[i];
            vector<int>& vals = tmp[i];

            bitset<1001> dp;
            dp[0] = 1;

            for (int v : vals) {
                dp |= (dp << v);
            }

            if (!dp[target]) return false;
        }

        return true;
    }
};

