class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:  
        @cache
        def backtrack(num, i, pos):
            if num == 0:
                return 0
            if pos == len(queries):
                return inf

            l, r, val = queries[pos]
            skip = 1 + backtrack(num, i, pos + 1)
            use = inf
            if l <= i <= r and num >= val:
                used = backtrack(num - val, i, pos + 1)
                if not isinf(used):
                    use = 1 + used
            return min(skip, use)

        min_ops = -float('inf')
        for i,num in enumerate(nums):
            res = backtrack(num, i, 0)
            if isinf(res):
                return -1
            min_ops = max(min_ops, res)
        return min_ops

