class Solution {
private:
    bool canFormSubsetSum(const vector<int>& coins, int target) {
        vector<bool> dp(target + 1, false);
        dp[0] = true;
        for (int coin : coins) {
            for (int j = target; j >= coin; j--) {
                dp[j] = dp[j] || dp[j - coin];
            }
        }
        return dp[target];
    }

    int check(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<int>> coins(n);

        for (int j = 0; j < k; j++) {
            int l = queries[j][0], r = queries[j][1], v = queries[j][2];
            for (int i = l; i <= r; i++) {
                coins[i].push_back(v);
            }
        }

        for (int i = 0; i < n; i++) {
            if (nums[i] != 0 && coins[i].empty()) return 1;
            int total = 0;
            for (int coin : coins[i])
                total += coin;
            if (total < nums[i]) return 1;

            if (!canFormSubsetSum(coins[i], nums[i]))
                return 1;
        }
        return 2;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();

        int l = 0, h = m;
        int ans = -1;
        while (l <= h) {
            int mid = l + (h - l) / 2;
            int res = check(nums, queries, mid);
            if (res == 2) {
                ans = mid;
                h = mid - 1; 
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

