class Solution {
    private Integer[][] memo;
    public int minZeroArray(int[] nums, int[][] queries) {
        int m = queries.length, n = nums.length;
        var vals = new ArrayList<List<Integer>>();
        for (int j = 0; j < n; j++) {
            vals.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int[] q = queries[i];
            for (int j = 0; j < n; j++) {
                if (q[0] <= j && j <= q[1]) {
                    vals.get(j).add(q[2]);
                } else {
                    vals.get(j).add(0); // added
                }
            }
        }
        int ans = -1;
        for (int j = 0; j < n; j++) {
            memo = new Integer[vals.get(j).size()][nums[j]+1];
            var res = dfs(0, nums[j], vals.get(j));
            if (res == (int) 1e9) {
                return -1;
            }
            ans = Math.max(ans, res);
        }
        return ans;
    }
    private int dfs(int i, int rem, List<Integer> vals) {
        if (rem == 0) {
            return i;
            // return i+1;
        } else if (i == vals.size()) {
            return (int) 1e9;
        } else if (memo[i][rem] == null) {
            var ans = dfs(i+1, rem, vals);
            if (vals.get(i) <= rem) {
                ans = Math.min(ans, dfs(i+1, rem - vals.get(i), vals));
            }
            memo[i][rem] = ans;
        }
        return memo[i][rem];
    }
}
