class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        # return min non-negative k, such that processing the first k queries in sequence, nums becomes zero array (all elements are 0)
        # if not exists, return -1
        # query [l, r, val] is a subset of indices in the range [l, r] from nums, decrement value at each index by exactly val

        # 1000 queries, nums in range 0->1000, nums.length only 10, val is only 10

        n = len(nums)
        if sum(nums) == 0:
            return 0
        iToPoss = [[False] * (x+1) for x in nums]
        # print(iToPoss)
        for arr in iToPoss:
            arr[0] = True
        remainInds = set()
        for i, val in enumerate(nums):
            if val > 0:
                remainInds.add(i)
        for idx, query in enumerate(queries):
            l, r, val = query
            for i in range(l, r+1):
                if i in remainInds:
                    poss = iToPoss[i]
                    for j in range(len(poss)-1, 0, -1):
                        if (j-val) < 0:
                            break
                        if poss[j-val]:
                            poss[j] = True
                    if poss[-1] == True:
                        remainInds.remove(i)
            # print(iToPoss)
            if len(remainInds) == 0:
                return idx + 1

        return -1

