class Solution:
    def minZeroArray(self, arr: List[int], q: List[List[int]]) -> int:
        n, m = len(arr), len(q)

        allzero = True
        for x in arr:
            if x != 0:
                allzero = False
                break
        if allzero:
            return 0

        def check3(target, vals):
            dp = [0]*(1+target)
            dp[0] = 1

            for v in vals:
                for i in range(target, v-1, -1):
                    dp[i] = max(dp[i], dp[i-v])
            #     print(f"check3 {v=} {dp=}")
            # print(f"{target=} {vals=} {dp=}")
            return dp[-1] == 1

        def check2(k, i):
            qs = []

            for l, r, v in q[:k+1]:
                if l <= i <= r:
                    qs.append(v)
            res = check3(arr[i], qs)
            # print(f"check2 {k=} {i=} {arr[i]=} {qs=} {res=}")
            return res

        def check(k):
            for i in range(n):
                if not check2(k, i):
                    return False
            return True

        # print(f"n{check(2)=}n")
        if not check(m-1):
            return -1

        l, r = 0, m-1
        while r - l >= 1:
            m = (l+r)//2

            if check(m):
                # print("check ok", l, r, m)
                r = m
            else:
                l = m+1

        return r+1

