class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size(), ans = 0; 

        for (int i = 0; i < n; ++i) {
            map<pair<int,int>, int> dp;
            function<int(int, int)> dfs = [&](int idx, int val) -> int {
                if (idx == m) return (val == 0) ? m : m+1;
                if (val == 0) return idx; 
                if(dp.find({idx, val}) != dp.end())
                    return dp[{idx, val}];
                int res = dfs(idx + 1, val);
                if (queries[idx][0] <= i && i <= queries[idx][1] && val >= queries[idx][2]) {
                    res = min(res, dfs(idx + 1, val - queries[idx][2]));
                }
                return dp[{idx, val}] = res;
            };

            int x = dfs(0, nums[i]);
            if (x > m) return -1;  
            ans = max(ans, x);
        }

        return ans;
    }
};

