class Solution(object):
    def minZeroArray(self, nums, queries):
        """
        :type nums: List[int]
        :type queries: List[List[int]]
        :rtype: int
        """
        toRemove = [[] for _ in xrange(len(nums) + 1)]
        toAdd = [[] for _ in xrange(len(nums) + 1)]
        vals = [0] * len(queries)
        n = max(nums)
        qI = 0
        currMax = 0
