                for j in toAdd[i]:
                    vals[j] = queries[j][2]
                for j in toAdd[i]:
                    for k in xrange(n - 1, -1, -1):
                        if dp[k]:
                            if vals[j] + k <= n:
                                dp[k + vals[j]] = True
            if len(toRemove[i]) > 0:
                for j in toRemove[i]:
                    vals[j] = 0
                dp = [False] * (n + 1)
                dp[0] = True
                for v in vals[:qI]:
                    for k in xrange(n - 1, -1, -1):
                        if dp[k]:
                            if v + k <= n:
                                dp[k + v] = True
                #print vals[:qI], dp

            while not dp[nums[i]]:
                if qI == len(queries):
                    return -1
                toAdd[queries[qI][0]].append(qI)
                toRemove[queries[qI][1] + 1].append(qI)
                if queries[qI][0] <= i <= queries[qI][1]:
                    v = queries[qI][2]
                    vals[qI] = v
                    for k in xrange(n - 1, -1, -1):
                        if dp[k]:
                            if v + k <= n:
                                dp[k + v] = True
                qI += 1
                #print dp, nums[i]
        return qI

