from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        size = len(nums)
        bit_masks = self.initialize_bit_masks(size)
        if self.are_all_zeros(nums):
            return 0
        for index, (start, end, value) in enumerate(queries):
            self.update_bit_masks(bit_masks, nums, start, end, value)

            if self.is_all_achieved(bit_masks, nums, size):
                return index + 1
        return -1

    def initialize_bit_masks(self, size: int) -> List[List[bool]]:
        bit_masks = [[False] * 1001 for _ in range(size)]
        for idx in range(size):
            bit_masks[idx][0] = True
        return bit_masks

    def are_all_zeros(self, nums: List[int]) -> bool:
        return all(num == 0 for num in nums)

    def update_bit_masks(self, bit_masks: List[List[bool]], nums: List[int], start: int, end: int, value: int) -> None:
        for idx in range(start, end + 1):
            for bit_position in range(1000, value - 1, -1):
                if bit_masks[idx][bit_position - value]:
                    bit_masks[idx][bit_position] = True
            for bit_position in range(nums[idx] + 1, 1001):
                bit_masks[idx][bit_position] = False

    def is_all_achieved(self, bit_masks: List[List[bool]], nums: List[int], size: int) -> bool:
        return all(bit_masks[idx][nums[idx]] for idx in range(size))

