class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        q = len(queries)
        n = len(nums)
        left, right = 0, q + 1

        def check(num: int) -> bool:
            cnt = [[] for _ in range(n)]
            for i in range(num):
                l, r, v = queries[i]
                for j in range(l, r + 1):
                    cnt[j].append(v)
            for i in range(n):
                target = nums[i]
                dp = {0}
                for val in cnt[i]:
                    new_dp = set(dp)
                    for s in dp:
                        new_sum = s + val
                        if new_sum <= target:
                            new_dp.add(new_sum)
                    dp = new_dp
                    if target in dp:
                        break
                if target not in dp:
                    return False
            return True

        res = -1
        while left < right:
            mid = (left + right) // 2
            if check(mid):
                res = mid
                right = mid
            else:
                left = mid + 1
        return res

