class Solution {
    static boolean checkSubsetSum(int sum,List<Integer> tt) {
        int n = tt.size();
        boolean[][] dp = new boolean[n + 1][sum + 1];
        for (int i = 0; i <= n; i++) {
            dp[i][0] = true;
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= sum; j++) {
                if (j < tt.get(i - 1)) {
                    dp[i][j] = dp[i - 1][j];
                }
                else {
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - tt.get(i - 1)];
                }
            }
        }

        return dp[n][sum];
    }
    static boolean check(List<List<Integer>> ls , int[] nums) {
        int n = nums.length;
        for(int i=0;i<n;i++) {
            if(checkSubsetSum(nums[i] , ls.get(i))) continue;
            else return false;
        }
        return true;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = -1;
        int cc =0;
        for(int i:nums) {
            if(i==0) cc++;
        }
        if(cc == nums.length) return 0;
        int k = 0;
        List<List<Integer>> ls = new ArrayList<>();
        for(int i=0;i<nums.length;i++) ls.add(new ArrayList<>());
        // System.out.println(ls);
        for(int[] i:queries) {
            for(int j=i[0];j<=i[1];j++) {
                ls.get(j).add(i[2]);
            }
            k++;
            if(check(ls,nums)) return k;
        }
        return -1;
    }
}

