    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<bool>> dp(n, vector<bool>(1001, false));

        for (int i = 0; i < n; i++) {
            dp[i][0] = true;
        }

        if (all_of(nums.begin(), nums.end(), [](int num) { return num == 0; })) {
            return 0;
        }

        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];

            for (int i = l; i <= r; i++) {
                for (int j = 1000; j >= 0; j--) {  
                    if (j >= v && dp[i][j - v]) {  
                        dp[i][j] = true;
                    } 
                    if (j > nums[i]) {  
                        dp[i][j] = false;
                    }
                }
