            bool allAchieved = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][nums[i]]) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }
};

