class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(x == 0 for x in nums):
            return 0
        max_j = -1
        for i in range(len(nums)):
            if nums[i] == 0:
                continue
            target = nums[i]
            Qi = []
            for j in range(len(queries)):
                l, r, val = queries[j]
                if l <= i <= r:
                    Qi.append((j, val))
            if not Qi:
                return -1
            dp = {0: -1}
            for (j, val) in Qi:
                new_dp = {}
                for s in dp:
                    current_max_j = dp[s]
                    if s in new_dp:
                        if current_max_j < new_dp[s]:
                            new_dp[s] = current_max_j
                    else:
                        new_dp[s] = current_max_j
                    new_s = s + val
                    if new_s > target:
                        continue
                    new_max_j = max(current_max_j, j)
                    if new_s in new_dp:
                        if new_max_j < new_dp[new_s]:
                            new_dp[new_s] = new_max_j
                    else:
                        new_dp[new_s] = new_max_j
                dp = new_dp
            if target not in dp:
                return -1
            current_max = dp[target]
            if current_max > max_j:
                max_j = current_max
        return max_j + 1 if max_j != -1 else 0

