class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0;
        int r = queries.size();
        int result = INT_MAX;
        while (l <= r) {
            int mid = (l + r) / 2;
            int res = helper(nums, queries, mid);
            if (res == -1) {
                l = mid + 1;
            } else {
                result = min(result, mid);
                r = mid - 1;
            }
        }
        if (result == INT_MAX) return -1;
        return result;
    }
    int helper(vector<int> nums, vector<vector<int>>& queries, int k) {
        vector<vector<int>> coins(nums.size());
        for (int q = 0; q < k; q++) {
            int left = queries[q][0];
            int right = queries[q][1];
            int val = queries[q][2];
            for (int i = left; i <= right; i++) coins[i].push_back(val);
        }
        for (int i = 0; i < nums.size(); i++) if (!makeable(coins[i], nums[i])) return -1;
        return 0;
    }
    int makeable(vector<int>& coins, int num) {
        vector<vector<int>> dp(num + 1, vector<int>(coins.size(), -1));
        return dfs(dp, coins, num, 0);
    }
    int dfs(vector<vector<int>>& dp, vector<int>& coins, int num, int i) {
        if (num < 0) return 0;
        if (num == 0) return 1;
        if (i == coins.size()) return 0;
        if (dp[num][i] != -1) return dp[num][i];
        return dp[num][i] = dfs(dp, coins, num - coins[i], i + 1) || dfs(dp, coins, num, i + 1);
    }
};

