class Solution {
public:

   int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
       int len = nums.size();
        int opCount = queries.size();
        vector<bitset<1001>> possibleVals(len);




        for (int idx = 0; idx < len; ++idx) {
            possibleVals[idx].set(0);
        }


        bool isZeroArray = true;
        for (int& val : nums) {
            if (val != 0) {
                isZeroArray = false;
                break;
            }
        }


        if (isZeroArray) return 0;


        for (int opIdx = 0; opIdx < opCount; ++opIdx) {
             auto& op = queries[opIdx];
            int leftBound = op[0];
             int rightBound = op[1];
             int addValue = op[2];


            for (int elemIdx = leftBound; elemIdx <= rightBound; ++elemIdx) {
                cout << 'v';


                bitset<1001> newPossibilities;


                for (int oldVal = 0; oldVal < 1001; ++oldVal) {
                    if (!possibleVals[elemIdx][oldVal]) continue;

                    if (oldVal + addValue < 1001) {
                        newPossibilities.set(oldVal + addValue);
                    }
                }


                possibleVals[elemIdx] |= newPossibilities;


                int targetVal = nums[elemIdx];
                for (int impossibleVal = targetVal + 1; impossibleVal < 1001; ++impossibleVal) {
                    possibleVals[elemIdx].reset(impossibleVal);
                }
            }



            bool success = true;
            for (int elemIdx = 0; elemIdx < len; ++elemIdx) {
                if (!possibleVals[elemIdx][nums[elemIdx]]) {
                    success = false;
                    break;
                }
            }


            if (success) {

                return opIdx + 1;
            }
        }

        return -1;
   }
};

