class Solution {
public:
    int dp[1001][1001];
    bool help(int indx, int tar, vector<int> &arr){
        if(tar==0) return true;
        if(indx>=arr.size()) return false;

        if(dp[indx][tar]!=-1) return dp[indx][tar];
        bool notpick=help(indx+1,tar,arr);
        bool pick=false;
        if(tar-arr[indx]>=0){
            pick=help(indx+1,tar-arr[indx],arr);
        }
        return dp[indx][tar]= notpick | pick;
    }
    bool check(vector<int>& nums, vector<vector<int>>& queries,int mid){
        map<int,vector<int>> mp;
        for(int it=0;it<mid;it++){
            for(int i=queries[it][0];i<=queries[it][1];i++){
                mp[i].push_back(queries[it][2]);
            }
        }
        int n=nums.size();
        for(int i=0;i<n;i++){
            int tar=nums[i];
            memset(dp,-1,sizeof(dp));
            if(help(0,tar,mp[i])){
                continue;
            }
            else{
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low=0,high=queries.size();
        int ans=INT_MAX;
        while(low<=high){
            int mid=(low+high)/2;
            if(check(nums,queries,mid)){
                high=mid-1;
                ans=min(ans,mid);
            }
            else low=mid+1;
        }
        return ans==INT_MAX ? -1 : ans;

    }
};

