#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    bool isSubsetSum(vector<int>& arr, int sum) {
        int N = arr.size();
        vector<vector<bool>> subset(N + 1, vector<bool>(sum + 1, false));

        for (int i = 0; i <= N; i++) subset[i][0] = true;

        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= sum; j++) {
                subset[i][j] = subset[i - 1][j];
                if (j >= arr[i - 1])
                    subset[i][j] = subset[i][j] || subset[i - 1][j - arr[i - 1]];
            }
        }
        return subset[N][sum];
    }

    bool poss(vector<int> &nums, int k, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> v(n);

        for (int i = 0; i < k; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for (int j = l; j <= r; j++) {
                v[j].push_back(val);
            }
        }

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) continue;
            if (v[i].empty() || !isSubsetSum(v[i], nums[i]))
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size(), ans = -1;

        while (r >= l) {
            int m = (r + l) / 2;
            if (poss(nums, m, queries)) {
                ans = m;
                r = m - 1;
            } else {
                l = m + 1;
            }
        }
        return ans;
    }
};

