class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

        int len = queries.length;
        if (!valid(nums, queries, len)) {
            //try all
            return -1;
        }
        int left = 0, right = len;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (valid(nums, queries, mid)) {
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return right + 1;
    }

    public boolean valid(int[] nums, int[][] q, int cnt) {
        for (int i = 0; i < nums.length; ++i) {
            if (!packValid(nums[i], q, cnt, i)) {
                return false;
            }
        }
        return true;
    }

    public boolean packValid(int sum, int[][] q, int cnt, int pos) {
        boolean[][] pack = new boolean[cnt + 1][sum + 1];
        pack[0][0] = true;
        for (int i = 0 ; i <= cnt; ++i) {
            for (int j = 0; j <= sum; ++j) {
                if (i == 0) {
                    continue;
                } else if (j == 0) {
                    pack[i][j] = true;
                } else {
                    if (pack[i - 1][j] || (q[i - 1][0] <= pos && q[i - 1][1] >= pos && j - q[i - 1][2] >= 0 && pack[i - 1][j - q[i - 1][2]])) {
                        pack[i][j] = true;
                    }
                }
            }

        }
        return pack[cnt][sum];
    }
}

