        for(auto it:nums){
            if(it==0){
                count++;
            }
        }
        if(count == nums.size()){
            return 0;
        }
        vector<bitset<1001>> dp(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            dp[i].reset();
            dp[i].set(0, true);
        }
        int k = 0;
        vector<int>arr(nums.begin(),nums.end());
        for(int i = 0; i< queries.size();i++){
            int x = queries[i][0];
            int y = queries[i][1];
            int val = queries[i][2];
            for (int j = x; j <= y; j++) {
                dp[j] |= (dp[j] << val);
                for (int x1 = nums[j]+1; x1 <1001; x1++) {
                    dp[j].reset(x1);
                }
            }
            int c1 = 0;
            for(int j = 0; j<nums.size();j++){
                if(dp[j].test(nums[j])){
                    c1++;
                }
            }
            k++;
            if(c1 == nums.size()){
                return k;
            }
        }
        return -1;
    }
};

