class Solution {
    private Map<String, Integer> memo = new HashMap<>();

    private int dp(int rem, int curIdx, int numIdx, int[][] queries) {
        if (curIdx == queries.length) {
            return (rem == 0) ? 0 : Integer.MAX_VALUE;
        }
        if (rem == 0) return 0;
        if (rem < 0) return Integer.MAX_VALUE;

        // Memoization key
        String key = rem + "," + curIdx + "," + numIdx;
        if (memo.containsKey(key)) return memo.get(key);

        int left = queries[curIdx][0];
        int right = queries[curIdx][1];
        int val = queries[curIdx][2];

        int ans = Integer.MAX_VALUE;

        // Use current query if numIdx is in the valid range
        if (left <= numIdx && numIdx <= right) {
            int useQuery = dp(rem - val, curIdx + 1, numIdx, queries);
            if (useQuery != Integer.MAX_VALUE) {
                ans = useQuery + 1;
            }
        }

        // Skip the current query
        int skipQuery = dp(rem, curIdx + 1, numIdx, queries);
        if (skipQuery != Integer.MAX_VALUE) {
            ans = Math.min(ans, skipQuery + 1);
        }

        memo.put(key, ans);
        return ans;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = -1;

        for (int i = 0; i < nums.length; i++) {
            int res = dp(nums[i], 0, i, queries);
            if (res == Integer.MAX_VALUE) return -1;  // If no valid solution, return -1
            ans = Math.max(ans, res);
        }

        return ans;
    }
}

